<?phpnamespace App\Models;use Database\Factories\StreakFactory;use Illuminate\Database\Eloquent\Factories\HasFactory;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\Relations\BelongsTo;class Streak extends Model{    /** @use HasFactory<StreakFactory> */    use HasFactory;    protected $fillable = [        'user_id',        'start_date',        'last_completed_date',        'current_streak',        'longest_streak',    ];    public function user():BelongsTo    {        return $this->belongsTo(User::class, 'user_id', 'id');    }}